import React, { useState } from "react";
import emailjs from 'emailjs-com';


function Contact() {
  cont [name, setName] = useState("");


  function handleSubmit(e) {
    e.preventDefault();
    console.log()
  }
  function sendEmail(e) {
    e.preventDefault(name, email, message);

    emailjs.sendForm('service_cud2i6m', 'template_r4c6dwx', e.target, 'user_Zq2hhsqAB2qox3FkQuK6y')
      .then((result) => {
          console.log(result.text);
      }, (error) => {
          console.log(error.text);
      });
  }

  return (
    <form className="contact-form" onSubmit={sendEmail} >
      <input type="hidden" name="contact_number" />
      <label>Name</label>
      <input type="text" name="user_name"  />
      <label>Email</label>
      <input type="email" name="user_email" />
      <label>Message</label>
      <textarea name="message" />
      <button type="submit" value="Send" class="btn btn-success">Submit</button>
    </form>
  );
  }


export default Contact;
