import React, { useState } from 'react';
import emailjs from 'emailjs-com';


function Contact() {

  const handleChange = (event) => {
    event.target.name=="name" 
    ? setName(event.target.value)
    : event.target.name=="email" 
    ? setEmail(event.target.value)
    : event.target.name=="message" 
    ? setMessage(event.target.value)
    : console.log("error")
  };

  function sendEmail(e) {
    e.preventDefault();

    emailjs.sendForm('service_cud2i6m', 'template_r4c6dwx', e.target, 'user_Zq2hhsqAB2qox3FkQuK6y')
      .then((result) => {
          console.log(result.text);
          result.text ==="OK" ? console.log("it workded") : alert("didn't work")
          setName("")
          setMessage
              }, (error) => {
          console.log(error.text);
      });
  }

  return (
    <form className="contact-form" onSubmit={sendEmail} href= "/">
      <input type="hidden" name="contact_number" />
      <label>Name</label>
      <input type="text" name="user_name"/>
      <label>Email</label>
      <input type="email" name="user_email" />
      <label>Message</label>
      <textarea name="message" />
      <button type="submit" value="Send" class="btn btn-success">Submit</button>
    </form>
  );
  }


export default Contact;
