import React from 'react';
import * as emailjs from 'emailjs-com'
import 'bootstrap/dist/css/bootstrap.min.css';
import { Form } from 'react-bootstrap';
import { render } from '@testing-library/react';

class Contact extends React.Component{
    constructor(props) {
        super(props);
        this.state = { feedback: '',
                      name: '',
                      email: '' ,
                      subject:'',
                      message:''
                     };

      this.handleSubmit = this.handleSubmit.bind(this);
      this.resetForm = this.resetForm.bind(this);
      this.handleChange = this.handleSubmit.bind(this);
    } 

    handleSubmit(event) {
      event.preventDefault();
      const { name, email, subject, message } = this.state;
      const templateParams = {
        from_name: name,
        from_email: email,
        to_name: '/*YOUR NAME OR COMPANY/',
        subject,
        message_html: message,
      };
      emailjs.send(
        'gmail',
        'template_cud2i6m',
        'templateParams',
        'user_Zq2hhsqAB2qox3FkQuK6y'
      )
      this.resetForm();
    };

    resetForm(){
      this.setState({
        name: '',
        email: '',
        subject:'',
        message:'',
      });
    }

    handleChange(event){
      this.setState({ [event.target.name]: event.target.value });
    }

    render(){
      const { name, email, subject, message, sentMessage } = this.state;
      return(
        <form onSubmit={this.handleSubmit}>
            <div class="form-group">
              <label for="exampleInputName">Name</label>
              <input name="name" type="text" class="form-control" id="exampleInputName" placeholder="Your first and last name" value={name} onChange={this.handleChange}/>
            </div>
            <div class="form-group">
              <label for="exampleInputEmail1">Email address</label>
              <input
                name="email"
                type="email"
                placeholder="email@gmail.com"
                value={email}
                onChange={this.handleChange}
                class="form-control"
                id="exampleInputEmail1"
                aria-describedby="emailHelp"
              />
            </div>
            <div class="form-group">
              <label for="exampleFormControlMessage">Message</label>
              <textarea
                name="message"
                placeholder="message"
                value={message}
                onChange={this.handleChange}
                class="form-control"
                id="exampleFormControlMessage"
              ></textarea>
            </div>

            <button type="submit" class="btn btn-success">Submit</button>
          </form>
      );
    }
  }


export default Contact;
